export const HSN_CODE_REGEX = /^\d{2}$|^\d{4}$|^\d{6}$|^\d{8}$/;

export function findHsnNode(code: string): HsnNode | undefined {
    if (!code) return undefined;
    const match = HSN_DICTIONARY.find(h => h.code === code);
    if (match) return match;

    // Fallback for custom/subheadings
    return {
        code,
        description: 'Custom / Subheading',
        type: code.length <= 2 ? 'chapter' : 'heading'
    };
}
